const updates = $input.first().json.result || [];
if (updates.length === 0) return [];

const results = [];
for (const update of updates) {
  if (update.message) {
    results.push({ json: { message: update.message, update_id: update.update_id } });
  }
}
return results;

const msg = $input.first().json.message;
const chatId = msg.chat.id;
const from = msg.from.username || msg.from.first_name;

// Check if voice message
if (msg.voice) {
  return [{ json: { type: 'voice', chatId, fileId: msg.voice.file_id, from } }];
}

// Check if text command
const text = (msg.text || '').trim();
if (!text.startsWith('/')) {
  return [{ json: { type: 'unknown', chatId, text, from } }];
}

const parts = text.split(' ');
const cmd = parts[0].toLowerCase().replace('/', '');
const args = parts.slice(1).join(' ');

return [{ json: { type: 'command', command: cmd, args, chatId, from } }];

const items = $input.all();
const containers = items.map(i => i.json);
const running = containers.filter(c => c.State === 'running');

let msg = '[Status] Container Health Check\n---\n';
msg += 'Time: ' + new Date().toLocaleString('zh-TW', { timeZone: 'Asia/Taipei' }) + '\n';
msg += 'Total: ' + containers.length + ' | Running: ' + running.length + '\n\n';

for (const c of containers) {
  const name = (c.Names && c.Names[0]) ? c.Names[0].replace('/', '') : 'unknown';
  const icon = c.State === 'running' ? '[OK]' : '[X]';
  msg += icon + ' ' + name + ' (' + c.State + ')\n';
}

return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId } }];

const items = $input.all();
const containers = items.map(i => i.json);
const mysql = containers.filter(c => c.Image && (c.Image.includes('mysql') || c.Image.includes('mariadb')));

let msg = '[Backup] Database Backup\n---\n';
msg += 'Time: ' + new Date().toLocaleString('zh-TW', { timeZone: 'Asia/Taipei' }) + '\n\n';

if (mysql.length === 0) {
  msg += 'No MySQL/MariaDB containers found.\n';
} else {
  msg += 'Found ' + mysql.length + ' database container(s):\n';
  for (const c of mysql) {
    const name = c.Names[0].replace('/', '');
    msg += '- ' + name + ' (backup triggered)\n';
  }
}

return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId } }];

let msg = '[News] Generating tech news summary...
---
';
msg += 'This will take 10-20 seconds. Please wait.
';
return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId } }];

const args = $node['Parse Message'].json.args;
let msg = '';

if (!args || args.trim() === '') {
  msg = '[Meeting] Please provide meeting notes
---
';
  msg += 'Usage: /meeting <your meeting notes>

';
  msg += 'Or send a voice message after typing /meeting';
  return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId, error: true } }];
}

const prompt = 'You are a professional meeting notes assistant. Please analyze the following meeting notes and provide a structured summary in Traditional Chinese.

Please include:
1. Meeting Summary (2-3 sentences)
2. Key Decisions (bullet points)
3. Action Items (with responsible persons if mentioned)
4. Next Steps (if mentioned)

Meeting Notes:
' + args;

return [{ json: { prompt, originalText: args, chatId: $node['Parse Message'].json.chatId } }];

const input = $input.first().json;
let summary = input.choices?.[0]?.message?.content || 'AI processing failed';
summary = summary.replace(/\*/g, '').replace(/_/g, ' ').replace(/`/g, '').replace(/\[/g, '(').replace(/\]/g, ')');

let msg = '[AI] Meeting Notes Summary\n---\nTime: ' + new Date().toLocaleString('zh-TW', { timeZone: 'Asia/Taipei' }) + '\n\n' + summary;

return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId } }];

let msg = '[MIS Assistant] Available Commands\n---\n';
msg += '/status - Check container status\n';
msg += '/backup - Run database backup now\n';
msg += '/news - Generate tech news summary\n';
msg += '/meeting <text> - AI meeting notes\n';
msg += '/help - Show this help\n\n';
msg += 'You can also send voice messages for meeting notes!';

return [{ json: { message: msg, chatId: $node['Parse Message'].json.chatId } }];

const text = $input.first().json.text || 'Voice transcription failed';
const chatId = $node['Parse Message'].json.chatId;

const prompt = 'You are a professional meeting notes assistant. Please analyze the following meeting notes and provide a structured summary in Traditional Chinese.

Please include:
1. Meeting Summary (2-3 sentences)
2. Key Decisions (bullet points)
3. Action Items (with responsible persons if mentioned)
4. Next Steps (if mentioned)

Meeting Notes:
' + text;

return [{ json: { prompt, originalText: text, chatId } }];
